/*===============================
        BASIC VARIABLES
=================================*/

const LIMIT_PAGINATION = 7;
const LIMIT_ITEM = 3;
let currentPage = 1;
let parameters = {
    page: 1,
    limit: 10,
    array: listItems,
    btnsArray: []
};
const nextBtn = document.querySelector(".next");
const prevBtn = document.querySelector(".prev");
const listContainer = document.querySelector(".emojis-container");
const btnsContainer = document.querySelector(".btns-pagination");

/*===============================
        PAGINATION FUNCTIONS
=================================*/

const setupPagination = () => {
    const { limit, array } = parameters;
    const start = 1;
    const end = LIMIT_PAGINATION + start - 1;
    const nbPages = Math.round(array.length / limit);
    createRangeButtons(start, end, nbPages);
    addClickOn(nextBtn, "next", nbPages);
    addClickOn(prevBtn, "prev", nbPages);
    selectedBtn();
};

const loadPagination = () => {
    const { page, limit, array } = parameters;
    const start = page * limit - limit;
    const end = start + limit;
    const elements = getElementsByRange(start, end, array);
    displayListItems(elements);
};

const addClickOn = (btn, state, nbPages) => {
    btn.addEventListener("click", () => {
        if (state === "next" && currentPage + 1 < nbPages) currentPage++;
        else if (state === "prev" && currentPage - 1 > 0) currentPage--;
        parameters.page = currentPage;
        updateButtons();
        loadPagination();
        selectedBtn();
    });
};

const getElementsByRange = (start, end, array) => {
    return array.slice(start, end);
};

/*===============================
        RENDER FUNCTIONS
=================================*/

const createRangeButtons = (start, end, nbPages) => {
    parameters.btnsArray = [];
    for (let i = start; i <= nbPages && i <= end; i++) {
        const btn = createButton(i);
        parameters.btnsArray.push(btn);
    }
    displayHTML(btnsContainer, parameters.btnsArray, true);
};

const createButton = (element) => {
    let btn = document.createElement("button");
    btn.innerText = element;
    btn.className = "btn";
    btn.addEventListener("click", () => {
        currentPage = element;
        parameters.page = currentPage;
        updateButtons();
        loadPagination();
        selectedBtn();
    });
    return btn;
};

const updateButtons = () => {
    const { array, limit } = parameters;
    const nbPages = Math.round(array.length / limit);
    let start = currentPage - LIMIT_ITEM;
    let end = currentPage + LIMIT_ITEM;

    start = start < LIMIT_ITEM - 1 ? 1 : start;
    end = start < LIMIT_ITEM - 1 ? LIMIT_PAGINATION + 1 : end;
    createRangeButtons(start, end, nbPages);
};

const displayListItems = (elements) => {
    listContainer.innerHTML = "";
    const list = elements.map((text) => `<div class="emojis-container__item">${text}</div>`);
    displayHTML(listContainer, list);
};

const displayHTML = (container, elements, append) => {
    container.innerHTML = "";
    elements.forEach((item) => {
        if (append) container.appendChild(item);
        else container.innerHTML += item;
    });
};

const selectedBtn = () => {
    const index = parameters.btnsArray.findIndex((item) => item.innerText == currentPage);
    parameters.btnsArray[index].className += " selected";
};

setupPagination();
loadPagination();
